import React from "react";
import { useNavigate, Link } from "react-router-dom";

const Header = () => {
  const menus = [
    {
      name: "Home",
      link: "/",
    },
    {
      name: "About",
      link: "/about-us",
    },
    {
      name: "Hostels",
      link: "/hostels",
    },
    {
      name: "Clients Testimonials",
      link: "/clients-testimonials",
    },
    {
      name: "Contact",
      link: "/contact-us",
    },
  ];

  const inputFields = [
    {
      label: "Keyword",
      placeholder: "Search by name",
    },
    {
      label: "Location",
      placeholder: "Search by location",
    },
    {
      label: "Area",
      placeholder: "Search by area",
    },
    {
      label: "Hostel Type",
      placeholder: "Search by hostel type",
    },
  ];
  const navigate = useNavigate();

  return (
    <div>
      <div className=" h-8 w-screen bg-red-300 flex items-center px-16">
        <div className="mr-12">Call us: 0900393743</div>
        <div>Chat us: area.wa</div>
        <div className=" ml-auto">
          <span className=" cursor-pointer" onClick={() => navigate("/login")}>
            Login
          </span>{" "}
          /{" "}
          <span
            className=" cursor-pointer"
            onClick={() => navigate("/register")}
          >
            Register
          </span>
        </div>
      </div>
      <div className="bg-white shadow-sm py-6 px-16 flex items-center justify-between">
        <div className=" text-6xl text-red-900 font-medium">HBM</div>
        <div className="flex item-center justify-evenly">
          {menus.map((menu, index) => (
            <div
              className="text-red-900 text-xl font-semibold ml-6"
              key={index}
            >
              <Link to={menu.link}>{menu.name}</Link>
            </div>
          ))}
        </div>
        <div className=" text-6xl text-red-900 font-medium">
          {inputFields.map((inputField, index) => (
            <div className="flex flex-col mr-4 w-[250px]" key={index}>
              <label className="text-red-900 font-bold">
                {inputField.label}
              </label>
              <input
                type="text"
                className="border border-gray-300 rounded-md p-2 mt-2"
                placeholder={inputField.placeholder}
              />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Header;
